package com.scaler.array;

public class VountStringSubsequence {
	
	public static int solve(String A) {
        int count = 0;
        int count_G = 0;
        int n = A.length();
        for(int i = n-1 ; i >= 0 ; i--){
            if(A.charAt(i) == 'G'){
               count_G++; 
            }else if(A.charAt(i) == 'A'){
               count+= count_G;
            }
        }
        return count;
    }
	
	public static void main(String[] args) {
		String A = "";
		System.out.println(solve( A));
	}

}
