package com.scaler.dp;

import java.util.Arrays;

public class RegularExpressionMatch {
	int [][] dp;
	public int isMatch(final String A, final String B) {
		int n = A.length();
		int m = B.length();
		dp = new int[n+1][m+1];
		for(int i = 0 ; i <= n ; i++) {
			Arrays.fill(dp[i], -1);
		}
		int reg =  regex(A, B, n-1, m-1);
		return reg > 0 ? 1 : 0;
	}
	int regex(String s, String p, int i, int j) { 
		if(i == -1 && j == -1)
			return 1; 
		if( j == -1 && i >= 0)
			return 0;
		if(i == -1 && j >= 0) {
			for(int k = 0 ; k <= j ; k++) {
				if(p.charAt(k) == '?' || Character.isLowerCase(p.charAt(k)))
					return 0;
			}
			return 1;
		}
		if(dp[i][j] != -1) {
			return dp[i][j];
		}
		if(s.charAt(i) == p.charAt(j))
			dp[i][j] = regex(s, p, i-1, j-1);
		if(s.charAt(i) != p.charAt(j)) {
			 if (Character.isAlphabetic(p.charAt(j))) {
				 return 0;
			 }else if (p.charAt(j) == '?') {
				 dp[i][j] = regex(s, p, i-1, j-1); 
			 }else if(p.charAt(j) == '*') {
				 dp[i][j] = regex(s, p, i-1, j) + regex(s, p, i, j-1);
			 }
		}
		return dp[i][j];
			
	}
	
	public static void main(String[] args) {
		System.out.println(new RegularExpressionMatch().isMatch("", 
				"a*"));
	}
}
